mapper.insert:
INSERT INTO MemberBadge (
    current_points,
    badge_level,
    member_id
) VALUES (
    #{current_points},
    #{badge_level},
    #{member_id}
);

mapper.update:
UPDATE MemberBadge 
SET current_points = #{current_points}, badge_level = #{badge_level}
WHERE member_id = #{member_id};

mapper.insert:
INSERT INTO UserActivityRecord (
    activity_type,
    points,
    created_date,
    member_id
) VALUES (
    #{activity_type},
    #{points},
    NOW(),
    #{member_id}
);
