mapper.insert:
INSERT INTO UserActivityRecord (
    activity_type,
    points,
    created_date,
    member_id
) VALUES (
    #{activity_type},
    #{points},
    NOW(),
    #{member_id}
);

mapper.select:
SELECT 'post' AS activityType, 0.5 AS points, created_date
FROM posts
WHERE member_id = #{value}

SELECT 'comment' AS activityType, 0.5 AS points, created_date
FROM comments
WHERE member_id = #{value}
