mapper.insert:
Insert INTO Comments (
    comments_contents,
    member_id,
    post_id
)
VALUES(
    #{comments_contents},
    #{member_id},
    #{post_id}
)

mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM Comments
    WHERE member_id = #{member_id}
    AND comments_id = #{comments_id}
) As existsLike

SELECT COUNT(*)
FROM Comments
WHERE post_id = #{post_id}
